import mongoose from 'mongoose';
import { ContactSchema } from '../models/crmModel';

const Contact = mongoose.model('Contact', ContactSchema);

// Ajout d'un nouveau contact
export const addNewContact = async (req, res) => {
    try {
        const newContact = new Contact(req.body);
        const contact = await newContact.save();
        res.status(200).json(contact); // Retourne le contact cr avec un statut 201
    } catch (err) {
        res.status(500).json({ message: 'Erreur lors de l\'ajout du contact', error: err.message });
    }
};

// Rcuprer tous les contacts
export const getContacts = async (req, res) => {
    try {
        const contacts = await Contact.find({}); // Pas de callback, juste await
        res.status(200).json(contacts); // Retourne les contacts trouvs avec un statut 200
    } catch (err) {
        res.status(500).json({ message: 'Erreur lors de la rcupration des contacts', error: err.message });
    }
};

// Rcuprer un contact par ID
export const getContactWithID = async (req, res) => {
    try {
        const contact = await Contact.findById(req.params.contactId); // Utilisation de await
        if (!contact) {
            return res.status(404).json({ message: 'Contact non trouv' });
        }
        res.status(200).json(contact); // Retourne le contact avec le statut 200
    } catch (err) {
        res.status(500).json({ message: 'Erreur lors de la rcupration du contact', error: err.message });
    }
};

// Mettre  jour un contact
export const updateContact = async (req, res) => {
    try {
        const contact = await Contact.findOneAndUpdate(
            { _id: req.params.contactId },
            req.body,
            { new: true } // Retourner le document mis  jour
        );
        if (!contact) {
            return res.status(404).json({ message: 'Contact non trouv pour la mise  jour' });
        }
        res.status(200).json(contact); // Retourne le contact mis  jour
    } catch (err) {
        res.status(500).json({ message: 'Erreur lors de la mise  jour du contact', error: err.message });
    }
};

// Supprimer un contact
export const deleteContact = async (req, res) => {
    try {
        const contact = await Contact.deleteOne({ _id: req.params.contactId });
        if (contact.deletedCount === 0) {
            return res.status(404).json({ message: 'Contact non trouv pour la suppression' });
        }
        res.status(200).json({ message: 'Contact supprim avec succs' });
    } catch (err) {
        res.status(500).json({ message: 'Erreur lors de la suppression du contact', error: err.message });
    }
};

